        ob_start();
        $return = B::displayVerification('sscanf', array ($mandate, "%s %d %d", &$month, &$day, &$year));
        $this->assertTrue($return === 3);
        $this->assertTrue($month === 'January');
        $this->assertTrue($day === 1);
        $this->assertTrue($year === 2000);
        $this->assertTrue(ob_get_clean() !== '');
    }

