        B::isUnitTestExeMode('DEBUG_UNIT_TEST');

        BU::$exeMode = B::UNIT_TEST;
        B::isUnitTestExeMode('DEBUG_UNIT_TEST');
        BU::$exeMode = B::REMOTE | B::UNIT_TEST;
        B::isUnitTestExeMode('DEBUG_UNIT_TEST');

        BU::$exeMode = 0;
        B::isUnitTestExeMode();
        BU::$exeMode = B::REMOTE;
        B::isUnitTestExeMode();

        BU::$exeMode = 0;
        B::isUnitTestExeMode('FALSE');
        BU::$exeMode = B::REMOTE;
        B::isUnitTestExeMode('FALSE');
    }

